                $len = (int) $tmp_len;

            }else{

                $len = (int) $len;

            }

        }

        switch($char_selection){
            case 0:

                $codeAlphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
                $codeAlphabet .= "abcdefghijklmnopqrstuvwxyz";
                $codeAlphabet .= "0123456789";

