                            //$tmp_detection_track_breadcrumb_ARRAY[] = '<br>' . __LINE__ . ' ISO STRING MATCH ON: ' . $unit_pattern . '. ';

                        }

                        $tmp_unit_system = 'ISO_80000';
                        $tmp_unit_index = self::$byte_units_ARRAY['ISO_80000']['CONTROL_PATTERN'][$unit_pattern];

