    private function return_thirdparty_text_link($system_asset_constant, $link_copy, $url, $resource_constant = CRNRSTN_RESOURCE_THIRDPARTY){

        // 'add_resource', CRNRSTN_RESOURCE_DOCUMENTATION
        switch($resource_constant){
            case CRNRSTN_RESOURCE_THIRDPARTY:

                if($this->content_output_type_ARRAY[$this->module_key] == 'sauce'){

                    return '<a href="' . $url . '">' . $link_copy . '</a>';

                }

                $tmp_img = $this->oCRNRSTN->return_system_image($system_asset_constant, '', 10, NULL, NULL, NULL, NULL, CRNRSTN_HTML);

                switch($system_asset_constant){
                    case 'SOCIAL_YOUTUBE':
                    default:

                        $tmp_link_id = '';
                        if(!isset($this->link_build_track_ARRAY[$system_asset_constant])){

                            $this->link_build_track_ARRAY[$system_asset_constant][] = 1;

                        }else{

                            $tmp_link_id = count($this->link_build_track_ARRAY[$system_asset_constant]);

                        }

                        $url = $this->oCRNRSTN->return_sticky_link($url, 'crnrstn_docs_' . $this->module_key . '_' . strtolower($system_asset_constant) . '_' . $tmp_link_id);
                        return '<a href="'. $url . '" target="_blank">' . $link_copy . '</a><sup class="crnrstn_the_r_text_link_sup">' . $tmp_img . '</sup>';

                    break;

                }

            break;
            default:

                //
                // HOOOSTON...VE HAF PROBLEM!
                $this->oCRNRSTN->error_log('Error building third party text link [' . $system_asset_constant . '] due to unknown $resource_constant [' . $resource_constant . '].', __LINE__, __METHOD__, __FILE__, CRNRSTN_SETTINGS_CRNRSTN);

            break;

        }

        return '';

    }

    public function return_crnrstn_text_link($module_key, $system_asset_constant = 'CRNRSTN_R_MD', $url_override = NULL, $resource_constant = CRNRSTN_RESOURCE_DOCUMENTATION){

        error_log(__LINE__ . ' csc new link html. $module_key[' . $module_key . ']. $system_asset_constant[' .  $system_asset_constant .  ']. $url_override[' . $url_override . ']. $resource_constant[' . $resource_constant . '].');
        die();

        switch($resource_constant){
            case CRNRSTN_RESOURCE_DOCUMENTATION:

                if($this->content_output_type_ARRAY[$this->module_key] == 'sauce'){

                    return $module_key;

                }

                $tmp_img = $this->oCRNRSTN->return_system_image($system_asset_constant, '', 10, NULL, NULL, NULL, NULL, CRNRSTN_HTML);

                if(isset($url_override)){

                    return '<a href="' . $url_override . '" target="_blank">' . $module_key . '</a><sup class="crnrstn_the_r_text_link_sup">' . $tmp_img . '</sup>';

                }

                return '<a href="#" onclick="oCRNRSTN_JS.related_link_text_click(\'' . $module_key . '\');">' . $module_key . '</a><sup class="crnrstn_the_r_text_link_sup">' . $tmp_img . '</sup>';

            break;
            case CRNRSTN_RESOURCE_OPENSOURCE:

                if(isset($this->module_key)){

                    if($this->content_output_type_ARRAY[$this->module_key] == 'sauce'){

                        if(isset($url_override)){

                           return '<a href="' . $url_override . '">' . $module_key . '</a>';

                        }

                        return '<a href="https://php.net/manual-lookup.php?pattern=' . $module_key . '">' . $module_key . '</a>';

                    }

                }

                $tmp_img = $this->oCRNRSTN->return_system_image($system_asset_constant, '', 10, NULL, NULL, NULL, NULL, CRNRSTN_HTML);

                switch($system_asset_constant){
                    case 'PHP_ELLIPSE':

                        if(isset($url_override)){

                            $tmp_url = $this->oCRNRSTN->return_sticky_link($url_override);

                            return '<a href="'. $tmp_url . '" target="_blank">' . $module_key . '</a><sup class="crnrstn_the_r_text_link_sup">' . $tmp_img . '</sup>';

                        }

                        $tmp_url = $this->oCRNRSTN->return_sticky_link('https://php.net/manual-lookup.php?pattern=' . $module_key);

                        error_log(__LINE__ . ' csc $tmp_url[' . strval($tmp_url) . ']. $module_key[' . strval($module_key)  . ']. content_output_type_ARRAY[' . print_r($this->content_output_type_ARRAY, true)  . '].');

                        die();

                        return '<a href="'. $tmp_url . '" target="_blank">' . $module_key . '</a><sup class="crnrstn_the_r_text_link_sup">' . $tmp_img . '</sup>';

                    break;

                }

            break;
            default:

                //
                // HOOOSTON...VE HAF PROBLEM!
                $this->oCRNRSTN->error_log('Error building text link [' . $module_key . '] due to unknown $resource_constant [' . $resource_constant . '].', __LINE__, __METHOD__, __FILE__, CRNRSTN_SETTINGS_CRNRSTN);

            break;

        }

        return '';

    }

